import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
    const { isAuthenticated, logout, userRole } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    return (
        <nav className="navbar"> {/* Izmenjeno iz Tailwind klasa u custom klasu */}
            <div className="container-fluid"> {/* Izmenjeno */}
                <Link to="/" className="navbar-brand">CryptoWallet</Link> {/* Izmenjeno */}
                <ul className="navbar-nav"> {/* Izmenjeno */}
                    {isAuthenticated ? (
                        <>
                            <li><Link to="/dashboard" className="nav-link">Dashboard</Link></li> {/* Izmenjeno */}
                            {userRole === 'admin' && (
                                <li><Link to="/admin" className="nav-link">Admin Panel</Link></li> 
                            )}
                            <li><button onClick={handleLogout} className="nav-button">Odjava</button></li> {/* Izmenjeno */}
                        </>
                    ) : (
                        <>
                            <li><Link to="/login" className="nav-link">Prijava</Link></li> {/* Izmenjeno */}
                            <li><Link to="/register" className="nav-link">Registracija</Link></li> {/* Izmenjeno */}
                        </>
                    )}
                </ul>
            </div>
        </nav>
    );
};

export default Navbar;
