import React, { useEffect, useState } from 'react';
import axios from 'axios';

// Simulacija fluktuacije cena za posebnu funkcionalnost
const simulatePriceChange = (originalPrice) => {
    const changePercentage = (Math.random() - 0.5) * 0.05; // +/- 5% fluktuacije
    return originalPrice * (1 + changePercentage);
};

const WalletOverview = ({ wallet, allCryptos }) => {
    const [cryptoPrices, setCryptoPrices] = useState({}); // {symbol: currentPriceUSD}

    useEffect(() => {
        // Inicijalno učitavanje cena
        const initialPrices = {};
        allCryptos.forEach(crypto => {
            initialPrices[crypto.symbol] = crypto.currentPriceUSD;
        });
        setCryptoPrices(initialPrices);

        // Simulacija real-time ažuriranja cena
        const interval = setInterval(() => {
            setCryptoPrices(prevPrices => {
                const newPrices = { ...prevPrices };
                allCryptos.forEach(crypto => {
                    // Ažurira na osnovu *originalne* cene iz db.json da ne bi odlutale previše
                    const originalCrypto = allCryptos.find(c => c.symbol === crypto.symbol);
                    if (originalCrypto) {
                        newPrices[crypto.symbol] = simulatePriceChange(originalCrypto.currentPriceUSD); 
                    }
                });
                return newPrices;
            });
        }, 5000); // Ažuriraj svakih 5 sekundi

        return () => clearInterval(interval); // Čisti interval
    }, [allCryptos]); // Ponovo pokreni ako se promeni lista kriptovaluta

    const totalValueUSD = Object.keys(wallet.balances).reduce((sum, symbol) => {
        const balance = wallet.balances[symbol];
        const price = cryptoPrices[symbol] || 0; // Koristi simuliranu cenu
        return sum + (balance * price);
    }, 0);

    return (
        <div className="bg-white p-6 rounded-lg shadow-xl mb-6">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Pregled Novčanika</h2>
            <div className="mb-4">
                <p className="text-gray-600">Ukupna vrednost:</p>
                <p className="text-4xl font-extrabold text-green-700">
                    ${totalValueUSD.toFixed(2)}
                </p>
            </div>
            <h3 className="text-xl font-semibold mb-3">Stanje po Kriptovalutama:</h3>
            <ul className="space-y-2">
                {Object.keys(wallet.balances).length > 0 ? (
                    Object.keys(wallet.balances).map(symbol => {
                        const balance = wallet.balances[symbol];
                        const price = cryptoPrices[symbol] || 0;
                        const value = balance * price;
                        return (
                            <li key={symbol} className="flex justify-between items-center p-2 border-b border-gray-100 last:border-b-0">
                                <span className="font-medium text-gray-700">{symbol}: <span className="text-blue-600">{balance.toFixed(4)}</span></span>
                                <span className="text-gray-500">
                                    (${price.toFixed(2)}/jedinica) <span className="font-semibold text-gray-800">${value.toFixed(2)}</span>
                                </span>
                            </li>
                        );
                    })
                ) : (
                    <p className="text-gray-600">Vaš novčanik je prazan.</p>
                )}
            </ul>
        </div>
    );
};

export default WalletOverview;
