import React from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { useAuth } from '../context/AuthContext'

const Navbar = () => {
    const { isAuthenticated, logout, userRole } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    return (
        <nav className="bg-gray-800 p-4 text-white">
            <div className="container mx-auto flex justify-between items-center">
                <Link to="/" className="text-2xl font-bold">CryptoWallet</Link>
                <ul className="flex space-x-4">
                    {isAuthenticated ? (
                        <>
                            <li><Link to="/dashboard" className="hover:text-gray-300">Dashboard</Link></li>
                            {userRole === 'admin' && (
                                <li><Link to="/admin" className="hover:text-gray-300">Admin Panel</Link></li>
                            )}
                            <li><button onClick={handleLogout} className="hover:text-gray-300">Odjava</button></li>
                        </>
                    ) : (
                        <>
                            <li><Link to="/login" className="hover:text-gray-300">Prijava</Link></li>
                            <li><Link to="/register" className="hover:text-gray-300">Registracija</Link></li>
                        </>
                    )}
                </ul>
            </div>
        </nav>
    );
};

export default Navbar;
