import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CryptoManagementPage = () => {
  const [cryptos, setCryptos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingCrypto, setEditingCrypto] = useState(null); // { id, symbol, name, currentPriceUSD }
  const [newSymbol, setNewSymbol] = useState('');
  const [newName, setNewName] = useState('');
  const [newPrice, setNewPrice] = useState('');
  const [addCryptoError, setAddCryptoError] = useState('');

  useEffect(() => {
    fetchCryptos();
  }, []);

  const fetchCryptos = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/cryptos');
      setCryptos(response.data);
    } catch (err) {
      setError('Greška pri učitavanju kriptovaluta.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateCrypto = async (e) => {
    e.preventDefault();
    setAddCryptoError('');
    if (!newSymbol || !newName || !newPrice) {
      setAddCryptoError('Sva polja moraju biti popunjena.');
      return;
    }
    const price = parseFloat(newPrice);
    if (isNaN(price) || price <= 0) {
      setAddCryptoError('Unesite validnu cenu.');
      return;
    }

    try {
      // Proveri da li simbol već postoji
      const existingCrypto = await axios.get(`http://localhost:5000/cryptos?symbol=${newSymbol.toUpperCase()}`);
      if (existingCrypto.data.length > 0) {
        setAddCryptoError('Simbol već postoji.');
        return;
      }

      const newCrypto = {
        id: `crypto${Date.now()}`, // Dodaj ID za json-server
        symbol: newSymbol.toUpperCase(),
        name: newName,
        currentPriceUSD: price
      };
      await axios.post('http://localhost:5000/cryptos', newCrypto);
      fetchCryptos();
      setNewSymbol('');
      setNewName('');
      setNewPrice('');
    } catch (err) {
      setAddCryptoError('Greška pri dodavanju kriptovalute.');
      console.error(err);
    }
  };

  const handleEditCrypto = (crypto) => {
    setEditingCrypto(crypto);
    setNewSymbol(crypto.symbol);
    setNewName(crypto.name);
    setNewPrice(crypto.currentPriceUSD);
  };

  const handleUpdateCrypto = async (e) => {
    e.preventDefault();
    setAddCryptoError('');
    if (!newSymbol || !newName || !newPrice || !editingCrypto) {
      setAddCryptoError('Sva polja moraju biti popunjena za ažuriranje.');
      return;
    }
    const price = parseFloat(newPrice);
    if (isNaN(price) || price <= 0) {
      setAddCryptoError('Unesite validnu cenu.');
      return;
    }

    try {
      await axios.put(`http://localhost:5000/cryptos/${editingCrypto.id}`, {
        symbol: newSymbol.toUpperCase(), // Možda ne bi trebalo menjati simbol? zavisi od logike
        name: newName,
        currentPriceUSD: price
      });
      fetchCryptos();
      setEditingCrypto(null);
      setNewSymbol('');
      setNewName('');
      setNewPrice('');
    } catch (err) {
      setAddCryptoError('Greška pri ažuriranju kriptovalute.');
      console.error(err);
    }
  };

  const handleDeleteCrypto = async (id) => {
    if (window.confirm('Da li ste sigurni da želite da obrišete ovu kriptovalutu?')) {
      try {
        await axios.delete(`http://localhost:5000/cryptos/${id}`);
        fetchCryptos();
      } catch (err) {
        setError('Greška pri brisanju kriptovalute.');
        console.error(err);
      }
    }
  };

  if (loading) {
    return <div className="text-center text-lg mt-8">Učitavanje kriptovaluta...</div>;
  }

  return (
    <div className="py-8">
      <h1 className="text-4xl font-bold text-gray-900 mb-8 text-center">Upravljanje Kriptovalutama</h1>

      {/* Forma za dodavanje/ažuriranje kriptovaluta */}
      <div className="bg-white p-6 rounded-lg shadow-xl mb-8">
        <h2 className="text-2xl font-semibold mb-4">{editingCrypto ? 'Ažuriraj Kriptovalutu' : 'Dodaj Novu Kriptovalutu'}</h2>
        <form onSubmit={editingCrypto ? handleUpdateCrypto : handleCreateCrypto}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="symbol">
              Simbol (npr. BTC):
            </label>
            <input
              type="text"
              id="symbol"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={newSymbol}
              onChange={(e) => setNewSymbol(e.target.value)}
              required
              disabled={!!editingCrypto}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="name">
              Naziv (npr. Bitcoin):
            </label>
            <input
              type="text"
              id="name"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={newName}
              onChange={(e) => setNewName(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="price">
              Trenutna Cena (USD):
            </label>
            <input
              type="number"
              id="price"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={newPrice}
              onChange={(e) => setNewPrice(e.target.value)}
              step="any"
              required
            />
          </div>
          {addCryptoError && <p className="text-red-500 text-xs italic mb-4">{addCryptoError}</p>}
          <div className="flex items-center justify-between">
            <button
              type="submit"
              className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-300"
            >
              {editingCrypto ? 'Ažuriraj' : 'Dodaj Kriptovalutu'}
            </button>
            {editingCrypto && (
              <button
                type="button"
                onClick={() => { setEditingCrypto(null); setNewSymbol(''); setNewName(''); setNewPrice(''); }}
                className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-300 ml-4"
              >
                Poništi
              </button>
            )}
          </div>
        </form>
      </div>

      {/* Lista kriptovaluta */}
      <div className="bg-white p-6 rounded-lg shadow-xl">
        <h2 className="text-2xl font-semibold mb-4">Lista Kriptovaluta</h2>
        {error && <p className="text-red-500 text-xs italic mb-4">{error}</p>}
        <table className="min-w-full bg-white">
          <thead>
            <tr>
              <th className="py-2 px-4 border-b text-left">Simbol</th>
              <th className="py-2 px-4 border-b text-left">Naziv</th>
              <th className="py-2 px-4 border-b text-left">Cena (USD)</th>
              <th className="py-2 px-4 border-b text-left">Akcije</th>
            </tr>
          </thead>
          <tbody>
            {cryptos.map(crypto => (
              <tr key={crypto.symbol} className="hover:bg-gray-50">
                <td className="py-2 px-4 border-b">{crypto.symbol}</td>
                <td className="py-2 px-4 border-b">{crypto.name}</td>
                <td className="py-2 px-4 border-b">${crypto.currentPriceUSD.toFixed(2)}</td>
                <td className="py-2 px-4 border-b">
                  <button
                    onClick={() => handleEditCrypto(crypto)}
                    className="bg-blue-500 hover:bg-blue-700 text-white text-sm py-1 px-2 rounded mr-2"
                  >
                    Uredi
                  </button>
                  <button
                    onClick={() => handleDeleteCrypto(crypto.id)} 
                    className="bg-red-500 hover:bg-red-700 text-white text-sm py-1 px-2 rounded"
                  >
                    Obriši
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CryptoManagementPage;
