import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <div className="min-h-[calc(100vh-80px)] flex flex-col items-center justify-center bg-gray-50 text-gray-800">
      <h1 className="text-5xl font-extrabold mb-6 text-indigo-700">Dobrodošli u CryptoWallet!</h1>
      <p className="text-lg text-center mb-8 max-w-2xl">
        Vaše sigurno mesto za praćenje i upravljanje digitalnim valutama. 
        Pratite svoje stanje, transakcije i budite u toku sa tržištem.
      </p>
      <div className="flex space-x-4">
        <Link 
          to="/register" 
          className="px-6 py-3 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 transition duration-300"
        >
          Registrujte se
        </Link>
        <Link 
          to="/login" 
          className="px-6 py-3 border border-indigo-600 text-indigo-600 font-semibold rounded-lg shadow-md hover:bg-indigo-100 transition duration-300"
        >
          Prijavite se
        </Link>
      </div>
    </div>
  );
};

export default HomePage;
